import {
    GET_POSTS,
    POST_ERROR
} from '../constants/types'

const initialState = {
    posts: [],
    post: null,
    loading: true,
    error: {}
}


export const Profile = (state= initialState,action) => {
    const { type, payload} = action;


    switch(type) {

        case GET_POSTS:

        return {
            ...state,
            posts :payload,
            loading:false
        };
        break;
        case GET_PROFILES:
            return {
                ...state,
                profiles: action.payload,
                loading: false
            };
        break;
        case GET_PROFILE:
        return {
            ...state,
            profile: action.payload,
            loading:false,
            error: null
        }

        break;
        case PROFILE_ERROR:

            return {
                ...state,
                error: action.payload,
                loading: false
            }

        break;
        case GET_REPOS:

            return {
                ...state,
                repos: action.payload,
                loading: false
            }
        break;
        case CLEAR_PROFILE:
            localStorage.removeItem('token')
            localStorage.removeItem('userdata')
            return {
                ...state,
                profile: null,
                repos: [],
                loading: false,
                error: null
            }
        break;
        default:
            return state;
    }
}
