import React from 'react'

import {Fragment,useState} from 'react'

import axios from 'axios';

import {Link } from 'react-router-dom'

import {Button} from 'react-bootstrap'

import { LoginAction } from '../Actions/auth';
export const Login = () => {

    const [formData,setFormData] = useState({
        email: '',
        password: '',
        password2: ''
    });


    const { email,password,password2} = formData;



    const OnChange = e => setFormData({...formData,[e.target.name]:e.target.value});

    const OnSubmit = async e => {
        e.preventDefault();
        console.log(email,password)
        if(password !== password2) {
            console.log("Passwords donot match")
        } else {
            
           const newUser = {
               email,
               password
           }
           try {

            const config = { headers: {
                'Content-Type': 'application/json'
            }
          }

            const body = JSON.stringify(newUser)

            const res = await axios.post('/api/auth',body,config)

            console.log("response",res)
           }catch(err) {
               console.log("server error")
           }
        }
    }
    return (
        <Fragment>
            
            <body>
                <nav className="navbar bg-dark">
                <h1>
                    <Link to="/"><i className="fas fa-code"></i> DevConnector</Link>
                </h1>
                <ul>
                    <li><a href="#4">Developers</a></li>
                    <li><Link to="/register">Register</Link></li>
                    <li><Link to="/login">Login</Link></li>
                </ul>
                </nav>
                <section className="container">
                <h1 className="large text-primary">Log in</h1>
                <p className="lead"><i className="fas fa-user"></i>Login to Your Account</p>
                <form className="form">
                    
                    <div className="form-group">
                    <input type="email" placeholder="Email Address" name="email"  onChange= {OnChange}/>
                    
                    </div>
                    <div className="form-group">
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        minLength="6"
                        onChange= {OnChange}
                    />
                    </div>
                    <div className="form-group">
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        name="password2"
                        minLength="6"
                        onChange= {OnChange}
                    />
                    </div>
                    <input type="submit" onSubmit = {OnSubmit} className="btn btn-primary" value="Login" />
                </form>
                <p className="my-1">
                    Not have an account? <Link to="/register">Sign In</Link>
                </p>
                </section>
            </body>
        </Fragment>
    )
}
