import {
    GET_POSTS,
    POST_ERROR,
    UPDATE_LIKES
} from '../constants/types'

const initialState = {
    posts: [],
    post: null,
    loading: true,
    error: {}
}


export const Posts = (state= initialState,action) => {
    const { type, payload} = action;


    switch(type) {

        case GET_POSTS:

        return {
            ...state,
            posts :payload,
            loading:false
        };
        break;
        
        case POST_ERROR:
            return {
                ...state,
                error: payload,
                loading:false
            };
        break;
        case UPDATE_LIKES:
            return {
                ...state,
                posts: state.posts.map(post => post._id === payload.id ? {
                    ...post,likes: payload.likes
                }: post),
                loading: false
            }
        break;
        default:
            return state;
    }
}
