import {
    REGISTER_SUCCESS,
    REGISTER_FAIL,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAIL,
    USER_LOADED,
    AUTH_ERROR
} from '../constants/types'

import setAuthToken from '../utils/setAuthToken'
import { useSelector } from 'react-redux'



export const UserInfoReducer = (state = initialState, action) => {

 

    switch(action.type) {
        case USER_LOADED:
            return {
                ...state,
                ...action.payload,
                isAuthenticated: true,
                loading:false
            }
        break;
        case AUTH_ERROR:
            //localStorage.removeItem('token');
            return {
                ...state,
                token: null,
                isAuthenticated: false,
                loading:false
            }
        break;
        default:
            return initialState;
    }
}
