import React from 'react'

import { Link, Redirect } from 'react-router-dom';
import PropTypes from 'prop-types';
import axios from 'axios';

import { useDispatch, useSelector } from 'react-redux'

import { SetAlert } from '../../Actions/alertAction';

import Alert from '../layout/Alert';

import { getCurrentProfile } from '../../Actions/profile';

import { useState } from 'react';

import { useEffect } from 'react';
import { Fragment } from 'react';

import { DashboardAction } from './DashboardAction'

import Moment from 'react-moment'

export const Experience = () => {

    const experience = useSelector(state => state.profile.profile.experience)
    
const experiences = experience.map(exp => (
    <tr key = {exp._id}>
        <td>{exp.company}</td>
        <td classNmae="hide-sm">{exp.title}</td>
        <td>
            <Moment format = 'YYYY/MM/DD'>{exp.from}</Moment> - {
                exp.to === null ? ('Now') : (<Moment format = 'YYYY/MM/DD'>{exp.to}</Moment>)
            }
        </td>
        <td>
            <button className="btn btn-danger">Delete</button>
        </td>
    </tr>
))
    return (
        <Fragment>
            <h2 classNmae="my-2">Experience credentials</h2>
            <table striped bordered hover responsive className="table-sm" classNmae="table">
                <thead className="table">
                    <tr>
                        <th>Comapny</th>
                        <th className="hide-sm">Title</th>
                        <th className="hide-sm">Years</th>
                    </tr>
                </thead>
                <tbody>
                    {experiences}
                </tbody>
            </table>
        </Fragment>
    )
}
