const Alert = () => {

    const [messg, setMessg] = useState('');
   const {msg,alertType,id} = useSelector(state => state.alert);

 

   useEffect(() => {
    if(msg) {
        setMessg(msg)
    }
}, [msg])
   console.log(msg)
