import axios from "axios";

import { SetAlert } from "./alertAction";
import {
   GET_POSTS,
   POST_ERROR
} from '../constants/types';


export const getPosts = () => async dispatch => {
    try {

        const res = await axios.get('/api/posts');

        dispatch({
            type: GET_POSTS,
            payload: res.data
        });
        
    }catch(err) {
        dispatch({
            type: POST_ERROR,
            payload: {msg: err.response,status:err.response}
        });
    }
};
