import { createStore, applyMiddleware,combineReducers} from "redux";

import { composeWithDevTools } from 'redux-devtools-extension'

import thunk from 'redux-thunk';

import { alertReducer } from "./Reducers/AlertReducer";

import { UserAuthReducer } from "./Reducers/authReducer";

import { UserInfoReducer } from "./Reducers/UserLoginReducer";

const initialState = [];

const middleware = [thunk];

const reducer = combineReducers({alert: alertReducer,loginAuth: UserAuthReducer,userInfo: UserLoginReducer})
const store = createStore(reducer,initialState,composeWithDevTools(applyMiddleware(...middleware)));


export default store;
