import { SET_ALERT,REMOVE_ALERT } from "../constants/types";



export const setAlert = (msg,alertType) => dispatch => {
    const id = "1234"

    dispatch({
        type: SET_ALERT,
        payload: { msg,alertType,id}
    });
}
