import React from 'react'

import { useSelector } from 'react-redux'
import { useEffect } from 'react'

import { useState } from 'react'

import { Fragment } from 'react'

import {getProfiles} from '../../Actions/profile'

import {Link,withRouter} from 'react-router-dom'

import { useDispatch } from 'react-redux'


import setAuthToken from "../../utils/setAuthToken";

import { getProfileById } from '../../Actions/profile'

import ProfileTop from './ProfileTop'
const Profile = ({match}) => {

    const dispatch = useDispatch()
    const {profile} = useSelector(state => state.profile)
    const userinfo = useSelector(state => state.userInfo)
    useEffect(() => {
        dispatch(getProfileById(match.params.id))
    },[getProfileById]);
    return (
        <Fragment>
           {profile !== null &&
           <Fragment>
               <hr></hr>
               <hr></hr>
               <Link style={{marginTop : '100px',marginLeft: '500px'}}to='/profiles' className="btn btn-primary">Back to profiles</Link>
               {(userinfo.isAuthenticated === true && userinfo.user._id === profile.user)  ? <Link to = '/edit-profile' className='btn btn-light'>Edit your Profile <PrrofileTop/></Link>  : (<div>You have not created the profile</div>)}
            </Fragment>
            }
        </Fragment>
    )
}


export default Profile
