import { GET_PROFILE,PROFILE_ERROR } from "../constants/types";

const initialState = {
    profile: null,
    profiles: [],
    repos: [],
    loading: true,
    error: {}
}

export const Profile = (state= initialState,action) => {
    const { type, payload} = action;


    switch(type) {
        case GET_PROFILE:

        return {
            ...state,
            profile: action.payload,
            loading:false
        }

        break;

        case PROFILE_ERROR:

        return {
            ...state,
            error: payload,
            loading: false
        }

        break;
        case CLEAR_PROFILE:
            return {
                ...state,
                profile: null,
                repos: [],
                loading: false
            }
        default:
            return state;
    }
}
