import { GET_PROFILE,PROFILE_ERROR } from "../constants/types";

const initialState = {
    profile: null,
    profiles: [],
    repos: [],
    loading: true,
    error: {}
}

export default function(state= initialState,action) {
    const { type, payload} = action;


    switch(type) {
        case GET_PROFILE:

        return {
            ...state,
            profile: action.payload,
            loading:false
        }

        break;

        case PROFILE_ERROR:

        return {
            ...state,
            error: payload,
            loading: false
        }

        break;
        default:
            return state;
    }
}
