import React from 'react'

import { Link } from 'react-router-dom'

import { useDispatch } from 'react-redux'

import { useSelector } from 'react-redux'

import { Logout } from '../../Actions/auth'

import { useState } from 'react'

import { Redirect } from 'react-router'

import { useEffect } from 'react'

import { GetUser } from '../../Actions/auth'

const Navbar = () => {

    const dispatch = useDispatch()

    const logindata = useSelector(state => state.userInfo)

    const isAuth = logindata.isAuthenticated

    //const [auth,setAuth] = useState('false')

    /*useEffect(() => {
       setAuth(isAuth)
    }, [logindata])*/

    const logout = () => {
        console.log("logout")
        dispatch(Logout())
    }

    if(logindata.isAuthenticated === false) {
        return <Redirect to ='/register'/>
    }
    const authLinks = (
        <ul>
            <li>
                <Link to ='/profiles'>
                    Developers
                </Link>
            </li>
            <Link to ='/dashboard' ><i className='fas fa-user'/>{' '}Dashboard</Link>
                <a onClick={logout}>
                    <i className="fas fa-sign-out-alt"></i>{' '}
                    <span className="hide-so">Logout</span>
                </a>
            </ul>
    )
    const guestLinks = (
        <ul>
            <li>
                <Link to ='/profiles'>
                    Developers
                </Link>
            </li>
            <li>
                <Link to="/register">Register</Link>
            </li>
            <li>
                <Link to="/login">Login</Link>
            </li>
        </ul>
    )
    return (
        <div>
            <nav className="navbar bg-dark">
            <h1>
                <Link to="/"><i class="fas fa-code"></i> DevConnector</Link>
            </h1>
            {isAuth && authLinks}
            {!isAuth && guestLinks}
            </nav>
        </div>
    )
}


export default Navbar
