import React from 'react'

import { useSelector } from 'react-redux'
import { useEffect } from 'react'

import { useState } from 'react'

import { Fragment } from 'react'

import {getProfiles} from '../../Actions/profile'

import {Link,withRouter} from 'react-router-dom'

import { useDispatch } from 'react-redux'

import ProfileItem from './ProfileItem'

import setAuthToken from "../../utils/setAuthToken";

export const Profiles = () => {

    const dispatch = useDispatch();

    const profileData = useSelector(state => state.profile)

    const { profiles, loading } = profileData

    const [data,setData] = useState('')

    useEffect(() => {
        dispatch(getProfiles())
        setData(profiles)
    },[loading]);


    console.log(data,"check",loading)
    
    return (
        <Fragment>
            { !loading &&
            (<div>
                <h1>hiiiii</h1>
                <h1 className='large text-primary'>Developers</h1>
                <p className='lead'>
                    <i className='fab fa-connect'></i>
                </p>
                    <div>{profiles.length}</div>
                    {console.log("length",profiles.length)}
                    {profiles.length > 0 ? (
                        profiles.map(data => {
                            <div className="profile bg-light">
            <img src={data.user.avatar} alt="" className="round-img"/>
            <div>
                {data.user.name}
                <p>{data.status} {data.company && <span>at {data.company}</span>}</p>
                <p className="my-1">{data.location && <span>{data.location}</span>}</p>
                <Link to ={`/profile/${data.user._id}`} className="btn btn-primary">
                    View profile
                </Link>
            </div>
            <ul>
                {data.skills.slice(0,4).map((skill,index) => (
                    <li key={index} className="text-primary">
                        <i className='fas fa-check' /> {skill}
                    </li>
                ))}
            </ul>
        </div>
                        })
                    ) : (<div>No profile found</div>)}
                
            </div>)}
        </Fragment>
       
    );
}
