import {
    REGISTER_SUCCESS,
    REGISTER_FAIL,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAIL,
    LOGOUT
} from '../constants/types'

const initialState = {
    token: null,
    isAuthenticated: null,
    loading: true,
    user: null
}


export const UserAuthReducer = (state = initialState, action) => {

    switch(action.type) {
        case REGISTER_SUCCESS:
            localStorage.setItem('token',action.payload);
            return {
                ...state,
                ...action.payload,
                isAuthenticated: true,
                loading:false
            }
           // dispatch(GetUser());
        break;
        case REGISTER_FAIL:
            localStorage.removeItem('token');
            return {
                ...state,
                token: null,
                isAuthenticated: false,
                loading:false
            }
        break;
        case USER_LOGIN_SUCCESS:
            console.log(action.payload.token.token)
            localStorage.setItem('token',action.payload.token);
            return {
                ...state,
                token:action.payload,
                isAuthenticated: true,
                loading:false
            }
        break;
        case USER_LOGIN_FAIL:
            localStorage.removeItem('token');
            return {
                ...state,
                token: null,
                isAuthenticated: false,
                loading:false
            }
        break;
        case LOGOUT:
            localStorage.removeItem('token');
            return {
                token: null,
                isAuthenticated: false,
                loading:false
            }
            
        default:
            return initialState;
    }
}
